// Banner.js
import React from 'react';

const Banner = ({ type, text, image, buttonText }) => {
  const bannerStyles = {
    background: type === 'gradient' ? 'linear-gradient(to right, #ff6b6b, #556270)' : 'none',
    backgroundImage: type === 'full-width' ? `url(${image})` : 'none',
  };

  return (
    <div className={`banner ${type}`} style={bannerStyles}>
      <div className="banner-content">
        {type === 'image' && <img src={image} alt="Banner" />}
        <p>{text}</p>
        {type === 'cta' && <button>{buttonText}</button>}
      </div>
    </div>
  );
};

export default Banner;
