import { useEffect, useState } from "react";
import NavigationBar from "../Components/NavigationBar";
import Footer from "../Components/Footer";
import "../CSS/LoginSignup.css";
import { Link } from "react-router-dom";
import { getAuth, signOut, createUserWithEmailAndPassword } from "firebase/auth";
import { doc, setDoc } from "firebase/firestore";
import { app, db } from "../Fileconfigsettings/firebaseconfig"; // Import your firebase app and firestore instance

function App() {
  const auth = getAuth();
  const [data, setData] = useState({
    email: "",
    password: "",
    name: "", // Added name state
    mobileNumber: "", // Added mobileNumber state
  });
  const [user, setUser] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleInputs = (event) => {
    let inputs = { [event.target.name]: event.target.value };
    setData({ ...data, ...inputs });
  };

  const isSignUpDisabled = !data.email || !data.password || !data.name || !data.mobileNumber; // Check if any field is empty

  const handleSignUp = async (e) => {
    e.preventDefault();
    try {
      // Create user with email and password
      const userCredential = await createUserWithEmailAndPassword(auth, data.email, data.password);

      // Save additional user info to Firestore
      await setDoc(doc(db, "users", userCredential.user.uid), { // Assuming 'users' is the collection name
        name: data.name,
        email: data.email,
        mobileNumber: data.mobileNumber,
      });

      // Clear form fields
      setData({
        email: "",
        password: "",
        name: "",
        mobileNumber: "",
      });

      // Redirect or show success message
      console.log('User signed up successfully!');
    } catch (error) {
      console.error('Error signing up:', error.message);
    }
  };

  const handlelogout = () => {
    signOut(auth);
  };

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      if (user) {
        alert("Account created for: "+user.email);
        setUser(user);
        setIsLoggedIn(true);
      } else {
        // alert("Not Logged In");
        setUser(null);
        setIsLoggedIn(false);
      }
    });

    return () => {
      // Unsubscribe the listener when the component is unmounted
      unsubscribe();
    };
  }, [auth]);

  return (
    <div className="WrapperMain">
      <NavigationBar />
      <h2>Welcome to BlogWiki</h2>
      <div className="FormWrapper">
        <div className="MainWrapper">
          <h2 className="loginHead">SIGNUP</h2>
          <form>
            <div className="EmailWrap">
              <label htmlFor="email">Email</label>
              <input
                placeholder="Email"
                name="email"
                type="email"
                className="Emailfield"
                value={data.email}
                onChange={(event) => handleInputs(event)}
              />
            </div>
            <div className="PasswordWrap">
              <label htmlFor="password">Password</label>
              <input
                placeholder="Password"
                name="password"
                type="password"
                className="Passwordfield"
                value={data.password}
                onChange={(event) => handleInputs(event)}
              />
            </div>
            <div className="NameWrap">
              <label htmlFor="name">Name</label>
              <input
                placeholder="Name"
                name="name"
                type="text"
                className="Namefield"
                value={data.name}
                onChange={(event) => handleInputs(event)}
              />
            </div>
            <div className="MobileNumberWrap">
              <label htmlFor="mobileNumber">Mobile Number</label>
              <input
                placeholder="Mobile Number"
                name="mobileNumber"
                type="text"
                className="MobileNumberfield"
                value={data.mobileNumber}
                onChange={(event) => handleInputs(event)}
              />
            </div>
          </form>
          <div className="LoginWrap">
            <button onClick={handleSignUp} className={`loginBtn ${isSignUpDisabled ? 'disabled' : ''}`} disabled={isSignUpDisabled}>
              SIGNUP
            </button>
            <button onClick={handlelogout} className={`loginBtn`}>LogOut</button>
          </div>
          <div className="LoginSignUpWrap">
            <p>
              already have an account?{" "}
              <Link className="LoginSignuplink" to="/LoginSignup">Login</Link>
            </p>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default App;
