import { useEffect, useState } from "react";
import NavigationBar from "../Components/NavigationBar";
import Footer from "../Components/Footer";
import "../CSS/LoginSignup.css";
import { Link } from "react-router-dom";
import { app } from "../Fileconfigsettings/firebaseconfig";
import {
  getAuth,
  signOut,
  onAuthStateChanged,
  createUserWithEmailAndPassword,
} from "firebase/auth";
import { doc } from "firebase/firestore";
function App() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [name, setName] = useState('');
    const [mobileNumber, setMobileNumber] = useState('');

    const handleSignUp = async (e) => {
        e.preventDefault();
        try {
            // Create user with email and password
            const userCredential = await firebase.auth().createUserWithEmailAndPassword(email, password);
            
            // Save additional user info to Firestore
            await firebase.firestore().collection('users').doc(userCredential.user.uid).set({
                name: name,
                email: email,
                mobileNumber: mobileNumber
                // You can add more fields as needed
            });

            // Clear form fields
            setEmail('');
            setPassword('');
            setName('');
            setMobileNumber('');

            // Redirect or show success message
            console.log('User signed up successfully!');
        } catch (error) {
            console.error('Error signing up:', error.message);
        }
    };


  const handlelogout = () => {
    signOut(auth);
  };
  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      if (user) {
        alert("Account created for: "+user.email);
        setUser(user);
        setIsLoggedIn(true);
      } else {
        // alert("Not Logged In");
        setUser(null);
        setIsLoggedIn(false);
      }
    });

    return () => {
      // Unsubscribe the listener when the component is unmounted
      unsubscribe();
    };
  }, [auth]);
  return (
    <div className="WrapperMain">
      <NavigationBar />
      <h2>Welcome to BlogWiki</h2>
      <div className="FormWrapper">
        <div className="MainWrapper">
          <h2 className="loginHead">SIGNUP</h2>
          <form>
            <div className="EmailWrap">
              <label For="email">Email</label>
              <input
                placeholder="Email"
                name="email"
                type="email"
                className="Emailfield"
                onChange={(event) => handleInputs(event)}
              />
            </div>
            <div className="PasswordWrap">
              <label htmlFor="password">Password</label>
              <input
                placeholder="Password"
                name="password"
                type="password"
                className="Passwordfield"
                onChange={(event) => handleInputs(event)}
              />
            </div>
          </form>
          <div className="LoginWrap">
          <button onClick={handleSignUp} className={`loginBtn ${isLoginDisabled ? 'disabled' : ''}`} disabled={isLoginDisabled}>
            SIGNUP
          </button>
          <button onClick={handlelogout} className={`loginBtn ${isLoginDisabled ? 'disabled' : ''}`} disabled={isLoginDisabled}>
            LogOut
          </button>
          </div>
          <div className="LoginSignUpWrap">
          <p>
                already have an account?{" "}
                <Link className="LoginSignuplink" to="/LoginSignup">Login</Link>
              </p>
            </div>
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default App;
